  `benefit_amount` decimal(12,2) NOT NULL COMMENT '商家优惠',
  `service_fee` decimal(12,2) NOT NULL COMMENT '手续费',
  `pay_biz` varchar(16) DEFAULT NULL COMMENT '收款工具（pos、插件、小程序、码牌）',
  `settle_type` varchar(10) DEFAULT NULL COMMENT '到账方式（T0、T1、D0、D1）',
